import React, { useEffect } from "react";
import CodeEditor, { SelectionText } from "@uiw/react-textarea-code-editor/dist/editor.css";
import "./ContentEditable.module.css";

function ContentEditable({value, onChange}) {
  const textRef = React.useRef();
  useEffect(() => {
    if (textRef.current) {
      const obj = new SelectionText(textRef.current);
      console.log("obj:", obj);
    }
  }, []);
  return ( 
      <div data-color-mode="dark">
        <label>
          
        <p style={{ margin: '0.5em 0'}}>Enter with your code</p>
        <CodeEditor
          value={value}
          ref={textRef}
          language="js"
          placeholder="try {
            const formData = new FormData()
            formData.append('title', title)
            formData.append('description', description)
            formData.append('image', file)
            const response = await fetch('http://localhost:9000/thoughts/register', {
              method: 'POST',
              // headers: {
              //   'Content-Type' : 'multipart/form-data'
              // },
              body: formData
              // body: JSON.stringify({
              //   title,
              //   description,
              //   file
              // })
            })
      
            const responseData = await response.json()
      "
          onChange={onChange}
          padding={15}
          style={{
            fontFamily:
              "ui-monospace,SFMono-Regular,SF Mono,Consolas,Liberation Mono,Menlo,monospace",
            fontSize: 12
          }}
        />
        </label>
      </div>
  );
}

export default ContentEditable
