import Table from "../components/Table"
import './Dashboard.css'
import './Container.css'
import { useState, useEffect } from "react"
import ErrorMessage from "../components/ErrorMessage"
import SuccessMessage from '../components/SuccessMessage'
import Input from "../components/Input"

const Dashboard = () => {
  const [thoughts, setThoughts] = useState([])
  const [error, setError] = useState('')
  const [success, setSuccess] = useState('')
  const [searchTerm, setSearchTerm] = useState('')

  useEffect(() => {
    async function getThoughts() {
      try {
        const response = await fetch('http://localhost:9000/thoughts/')
        const responseData = await response.json()

        if (!response.ok) {
          throw new Error(responseData.message)
        }
        setThoughts(responseData)
      } catch (err) {
        setError(err.message)
      }
    }
    getThoughts()
  }, [])

  const value = thoughts.thoughts
  return (
    <>
    <div className="container">
      <h1>Dashboard</h1>
      {error && <ErrorMessage>{error}</ErrorMessage>}
      {success && <SuccessMessage>{success}</SuccessMessage>}
      <Input type="text" name="searchTerm" id="searchTerm" placeholder='Search any thought' onChange={e => setSearchTerm(e.target.value)}/>
    </div>
    <div className="title-container">
      {Array.isArray(value) && value.filter(val => {
        if (searchTerm === "") {
          return val
        } else if (val.title.toLowerCase().includes(searchTerm.toLowerCase())) {
          return val
        }
        return val
      }).map(thought => (
        <div className="table">
          <Table
          id={thought._id}
          title={thought.title}
          description={thought.description}
          to={`/edit/${thought._id}`}
          onSubmit={async (e) => {
            e.preventDefault()
            try {
              const response = await fetch(`http://localhost:9000/thoughts/delete/${thought._id}`, {
                method: 'DELETE'
              })
              const responseData = await response.json()
              if (!response.ok) {
              throw new Error(responseData.message) 
              }
              setSuccess(responseData.message)
              window.location.reload()
            } catch (err) {
              setError(err.message)
            }
          }}
          type='submit'
          value='Delete'
        />
        </div>
        
      ))}
    </div>
    </>
  )
  
}

export default Dashboard
