import { useState } from 'react';
import './index.css'

function App() {
  const [title, setTitle] = useState('')
  const [description, setDescription] = useState('')

  const handleSubmit = (e) => {
    e.preventDefault()
    console.log(title);
    console.log(description);
  }

  return (
    <div className="App">
      <div>
        <h1>Create your thoughts</h1>
      </div>
      <form onSubmit={handleSubmit}>
        <div>
          <input type="text" name="title" id="title" placeholder="Enter your thought!" onChange={e => setTitle(e.target.value)}/>
        </div>
        <div>
          <input type="text" name="description" id="description" placeholder="Enter your description!" onChange={e => setDescription(e.target.value)}/>
        </div>
        <div>
          <input type="submit" value="Create" />
        </div>
      </form>
    </div>
  );
}

export default App;
