import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import ErrorMessage from "./ErrorMessage"
import SuccessMessage from "./SuccessMessage"
import styles from './Table.module.css'

const Table = () => {
  const [thoughts, setThoughts] = useState([])
  const [error, setError] = useState('')
  const [success, setSuccess] = useState('')

  useEffect(() => {
    async function getThoughts() {
      try {
        const response = await fetch('http://localhost:9000/thoughts/')
        const responseData = await response.json()

        if (!response.ok) {
          throw new Error(responseData.message)
        }
        setThoughts(responseData)
      } catch (err) {
        setError(err.message)
      }
    }
    getThoughts()
  }, [])

  const value = thoughts.thoughts

  // async function handleSubmit(e) {
  //   e.preventDefault()
  //   try {
  //     const id = value.map(thought => thought._id)
  //     console.log(id)
  //     const response = await fetch(`http://localhost:9000/thoughts/delete/${id}`, {
  //       method: 'DELETE'
  //     })
  //     const responseData = await response.json()
  //     if (!response.ok) {
  //     throw new Error(responseData.message) 
  //     }
  //     setSuccess(responseData.message)
  //   } catch (err) {
  //     setError(err.message)
  //   }
  // }

  //console.log(Object.keys(value));
  return (
    <div>
      {error && <ErrorMessage>{error}</ErrorMessage>}
      {success && <SuccessMessage>{success}</SuccessMessage>}
      {Array.isArray(value) && value.map((thought, i) => (
          <div className={styles.table} key={i}>
            <h4>ID: {thought._id}</h4>
            <h3>TITLE: {thought.title}</h3>
            <p>DESCRIPTION: {thought.description}</p>
            <Link to={`/edit/${thought._id}`}>Edit</Link>
            <form onSubmit={async () => {
              try {
                const response = await fetch(`http://localhost:9000/thoughts/delete/${thought._id}`, {
                  method: 'DELETE'
                })
                const responseData = await response.json()
                if (!response.ok) {
                throw new Error(responseData.message) 
                }
                setSuccess(responseData.message)
              } catch (err) {
                setError(err.message)
              }
            }}>
              <input type='submit' value='Delete'/>
            </form>
          </div>
        )
      )}
      <div className={styles.table}>
        <h4>Title</h4>
        <p>Description</p>
        <Link >Edit</Link>
        <form >
          <input type="submit" value="Delete" />
        </form>
      </div>
    </div>
  )
}

export default Table
