import styles from './Input.module.css'
const Textarea = ({ text, name, id, placeholder, value, onChange }) => {
  return (
    <div className={styles.input_container}>
      <label>
        <span>{text}:</span>
        <textarea 
          name={name} 
          id={id} 
          placeholder={placeholder} 
          onChange={onChange} 
          value={value}
        >        
        </textarea>
      </label>
    </div>
  )
}

export default Textarea
