import Table from "../components/Table"
import { useState, useEffect } from "react"
import ErrorMessage from "../components/ErrorMessage"
import Input from "../components/Input"
import Modal from "../components/Modal"
import styles from './Dashboard.module.css'

const Dashboard = () => {
  const [thoughts, setThoughts] = useState([])
  const [error, setError] = useState('')
  const [search, setSearch] = useState('')
  const [filterThoughts, setFIlterThoughts] = useState([])
  const [modalVisible, setModalVisible] = useState(false)


  useEffect(() => {
    async function getThoughts() {
      try {
        const response = await fetch('http://localhost:9000/thoughts/')
        const responseData = await response.json()

        if (!response.ok) {
          throw new Error(responseData.message)
        }
        setThoughts(responseData)
      } catch (err) {
        setError(err.message)
      }
    }
    getThoughts()
  }, [])

  useEffect(() => {
    setFIlterThoughts(
      Array.isArray(thoughts.thoughts) && thoughts.thoughts.filter(thought => {
        return thought.title.toLowerCase().includes(search.toLocaleLowerCase())
      })
    )
  }, [search, thoughts])



  return (

      <div className={styles.container}>
        <h1>Dashboard</h1>
        {error && <ErrorMessage>{error}</ErrorMessage>}
        {!error 
          ? <Input 
              type="text" 
              name="search" 
              id="search" 
              placeholder='Search any thought' 
               onChange={e => setSearch(e.target.value)}
            /> 
          : ''
        }
      
        {!error 
          ? Array.isArray(filterThoughts) 
          && filterThoughts.length > 0 
            ? filterThoughts.map((thought, i) => (
                <div key={thought._id}>
                  <Table
                    key={thought._id}
                    id={thought._id}
                    title={thought.title}
                    description={thought.description}
                    to={`/edit/${thought._id}`}
                    onSubmit={
                      () => setModalVisible(true)
                    }
                    type='submit'
                    value='Delete'
                  />
                  {modalVisible && 
                    <Modal
                      key={thought._id}
                      onClose={() => setModalVisible(false)}
                      
                      value='d'
                      onSubmit={async (e) => {
                        e.preventDefault()
                        try {
                          const response = await fetch(`http://localhost:9000/thoughts/delete/${thought._id}`, {
                            method: 'DELETE'
                          })
                          const responseData = await response.json()
                          if (!response.ok) {
                          throw new Error(responseData.message) 
                          }
                          window.location.reload()
                        } catch (err) {
                          setError(err.message)
                        }
                      }}
                    />
                  }
                </div>
              )) 
            : <h1>Thought not found!</h1>
          : ''
        }
        
      </div>
  )
}

export default Dashboard
