import Input from "../components/Input"
import Textarea from "../components/Textarea"
import ErrorMessage from "../components/ErrorMessage"
import SuccessMessage from "../components/SuccessMessage"
import { useState, useEffect } from "react"
import { useParams } from "react-router-dom"


const Update = () => {
  const [error, setError] = useState('')
  const [success, setSuccess] = useState('')
  const [thoughts, setThoughts] = useState([])
  const value = thoughts.thoughts


  const array = Array.isArray(value) && value.map(thought => {
      const titles = thought.title
      return titles
    })
  
  console.log(array)
  
  const {id} = useParams()
  const [title, setTitle] = useState(array)
  const [description, setDescription] = useState('')

  //console.log(id)
  useEffect(() => {
    async function getThoughts() {
      try {
        const response = await fetch('http://localhost:9000/thoughts/')
        const responseData = await response.json()

        if (!response.ok) {
          throw new Error(responseData.message)
        }
        setThoughts(responseData)
      } catch (err) {
        setError(err.message)
      }
    }
    getThoughts()
  }, [])

  

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
        const response = await fetch(`http://localhost:9000/thoughts/edit/${id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
          title,
          description
        })
      })

      const responseData = await response.json()
      console.log(responseData)
    

      if (!response.ok) {
        throw new Error(responseData.message)
      } else {
        setError('')
        setSuccess(responseData.message)
        setTitle('')
        setDescription('')
        setThoughts(responseData)
      }
    } catch (err) {
      setSuccess('')
      setError(err.message)
    }
  }
  
  return (
    <div className="container">
      {error && <ErrorMessage>{error}</ErrorMessage>}
      {success && <SuccessMessage>{success}</SuccessMessage>}
      <h1>Edit</h1>
      <form onSubmit={handleSubmit}>
        <Input
          text='Title'
          type='text'
          name='title'
          id='title'
          placeholder='Enter with a title for the thought!'
          onChange={e => setTitle(e.target.value)}
          value={title}
        />
        <Textarea
          text='Description'
          type='text'
          name='description'
          id='description'
          placeholder='Enter with a description for the thought!'
          onChange={e => setDescription(e.target.value)}
          value={description}
        />

        <input className="btn" type="submit" value="Register" />
      </form>
    </div>
  )
}

export default Update
