import { useState, useRef } from "react"
import ErrorMessage from "../components/ErrorMessage"
import Input from "../components/Input"
import SuccessMessage from "../components/SuccessMessage"
import Textarea from "../components/Textarea"
import styles from './Register.module.css'
import Submit from "../components/Submit"
import { useNavigate } from "react-router-dom"
import InputImage from "../components/InputImage"
import ContentEditable from "../components/ContentEditable"
import JoditEditor from 'jodit-react'

const Register = () => {
  const [title, setTitle] = useState('')
  const [description, setDescription] = useState('')
  const [error, setError] = useState('')
  const [success, setSuccess] = useState('')
  const [file, setFile] = useState([])
  const [content, setContent] = useState('')

  const navigate = useNavigate()

  const handleSubmit = async (e) => {
    e.preventDefault()
    
    try {
      const formData = new FormData()
      formData.append('title', title)
      formData.append('description', description)
      formData.append('image', file)
      const response = await fetch('http://localhost:9000/thoughts/register', {
        method: 'POST',
        // headers: {
        //   'Content-Type' : 'multipart/form-data'
        // },
        body: formData
        // body: JSON.stringify({
        //   title,
        //   description,
        //   file
        // })
      })

      const responseData = await response.json()

      if (!response.ok) {
        throw new Error(responseData.message)
      } else {
        navigate('/', {
          state: {
            message: responseData.message
          }
        })

        // setError('')
        // setSuccess(responseData.message)
        // setTimeout(() => {
        //   setSuccess('')
        // }, 1000);
        // setTitle('')
        // setDescription('')
      }
    } catch (err) {
      setSuccess('')
      setError(err.message)
    }
  }

  const onInputChange = (e) => {
    setFile(e.target.files[0])
    console.log(file.name)
  }

  const editor = useRef(null)

  const handleUpdate = (e) => {
    const editorContent = e.target.value
    setContent(editorContent)
  }

  console.log(content)
  
  return (
    <div className={styles.container}>
      <h1>Register</h1>
      <form onSubmit={handleSubmit}>
        <Input
          text='Title'
          type='text'
          name='title'
          id='title'
          placeholder='Enter with a title for the thought!'
          onChange={e => setTitle(e.target.value)}
          value={title}
        />
        <Textarea
          text='Description'
          type='text'
          name='description'
          id='description'
          placeholder='Enter with a description for the thought!'
          onChange={e => setDescription(e.target.value)}
          value={description}
        />
        <InputImage 
          onChange={onInputChange} 
          type="file" 
          value="Choose file" 
          name='image'
          accept='.jpg, .png, .jpeg' 
          fileName={file.name}
        />
        {/* <ContentEditable
          onChange={e => setContent(e)}
          config={config}
          value={content}
          ref={editor}
        /> */}
        <JoditEditor
          //onChange={newContent => {}}
          onBlur={handleUpdate}
          //config={config}
          value={content}
          ref={editor}
        />
        <Submit type="submit" value="Register" />
      </form>
      <h1>
        <button onClick={() => console.log(content)}></button>{content}
      </h1>
      {error && <ErrorMessage>{error}</ErrorMessage>}
      {success && <SuccessMessage>{success}</SuccessMessage>}
    </div>
  )
}

export default Register
