import Input from "../components/Input"
import Textarea from "../components/Textarea"
import ErrorMessage from "../components/ErrorMessage"
import SuccessMessage from "../components/SuccessMessage"
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import styles from './Update.module.css'
import Submit from "../components/Submit"
import InputImage from "../components/InputImage"

const Update = () => {
  const [error, setError] = useState('')
  const [success, setSuccess] = useState('')
  const [thoughts, setThoughts] = useState([])
  const {id} = useParams()
  const [title, setTitle] = useState('')
  const [description, setDescription] = useState('')
  const [file, setFile] = useState([])
  
  useEffect(() => {
    async function edit() {
      const response = await fetch(`http://localhost:9000/thoughts/${id}`)
      const responseData = await response.json()
      setThoughts(responseData)
    }
    edit()
  }, [id])
  
  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      const formData = new FormData()
      formData.append('title', title)
      formData.append('description', description)
      formData.append('image', file)
        const response = await fetch(`http://localhost:9000/thoughts/edit/${id}`, {
        method: 'PATCH',
        body: formData
        // headers: {
        //   'Content-Type' : 'application/json'
        // },
        // body: JSON.stringify({
        //   title,
        //   description
        // })
      })

      const responseData = await response.json()
    
      if (!response.ok) {
        throw new Error(responseData.message)
      } else {
        setError('')
        setSuccess(responseData.message)
        setTimeout(() => {
          setSuccess('')
        }, 1000);
        setTitle('')
        setDescription('')
      }
    } catch (err) {
      setSuccess('')
      setError(err.message)
    }
  }

  const onInputChange = (e) => {
    setFile(e.target.files[0])
    console.log(file.name)
  }

  //console.log(thoughts.thought)
  
  return (
    <div className={styles.container}>
      <h1>Edit</h1>
      <form onSubmit={handleSubmit}>
        <Input
          text='Title'
          type='text'
          name='title'
          id='title'
          placeholder={thoughts.thought?.title || ''}
          onChange={e => setTitle(e.target.value)}
          value={title}
        />
        <Textarea
          text='Description'
          type='text'
          name='description'
          id='description'
          placeholder={thoughts.thought?.description || ''}
          onChange={e => setDescription(e.target.value)}
          defaultValue={thoughts.thought?.description || ''}
          value={description}
        />
        <InputImage 
          onChange={onInputChange} 
          type="file" 
          value="Choose file" 
          name='image'
          accept='.jpg, .png, .jpeg' 
          fileName={file.name}
        />
        <Submit type="submit" value="Edit" />
      </form>
      {error && <ErrorMessage>{error}</ErrorMessage>}
      {success && <SuccessMessage>{success}</SuccessMessage>}
      {
        thoughts
      }
      

      <img src="" alt="" />
    </div>
  )
}

export default Update
