import styles from './Input.module.css'

const Input = ({text, type, name, id, placeholder, value, onChange}) => {
  return (
    <div className={styles.input_container}>
      <label htmlFor='title'> {text}: </label>
      <input type={type} name={name} id={id} placeholder={placeholder} value={value} onChange={onChange}/>
      <label>{text}:</label>
      <textarea name={name} id={id} placeholder={placeholder} onChange={onChange}>{value}</textarea>
    </div>
  )
}

export default Input
