import styles from './InputImage.module.css'

const InputImage = ({value, onChange, ref}) => {
  return (
    <div className={styles.image_file}>
      <input 
          ref={ref} 
          type="file" 
          name="image" 
          accept={`${'.jpg'},${'.png'}`} 
          id="image" 
          onChange={onChange}
          style={{display: 'none'}}
        />
      <button className={styles.image_file_btn}>{value}</button>
    </div>
  )
}

export default InputImage
