import Editor from 'react-simple-code-editor'
import { useState } from 'react';

const EditorPrism = () => {
  const [code, setCode] = useState('');
  return (
    <div>
      <Editor
      value={code}
      onValueChange={code => setCode(code)}
      highlight={(code) => { }}
      padding={10}
      style={{
      fontFamily: '"Fira code", "Fira Mono", monospace',
      fontSize: 12
    }}
  />
    </div>
  )
}

export default EditorPrism
