import { useRef } from 'react'
import styles from './InputImage.module.css'


const InputImage = ({value, onChange, type, accept, name, onClick}) => {
  const refPick = useRef()

  const pickImage = () => {
    refPick.current.click()
  }
  return (
    <div className={styles.image_file}>
      <input  
          ref={refPick}
          type={type} 
          name={name} 
          accept={accept} 
          id="image" 
          onChange={onChange}
          style={{display: 'none'}}
        />
      <a onClick={pickImage} className={styles.image_file_btn}>{value}</a>
      <p className={styles.image_file_p}>asjkhdkjalsd</p>
    </div>
  )
}

export default InputImage
