const Textarea = ({ text, name, id, placeholder, value, onChange }) => {
  return (
    <div>
      <label>{text}</label>
      <textarea 
        name={name} 
        id={id} 
        placeholder={placeholder} 
        onChange={onChange} 
        value={value}
      >        
      </textarea>
    </div>
  )
}

export default Textarea
