import { useState } from 'react';
import './index.css'

function App() {
  const [title, setTitle] = useState('')
  const [description, setDescription] = useState('')
  const [error, setError] = ('')

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      const response = await fetch('http://localhost:9000/thoughts/register', {
        method: 'POST',
        headers: {
          'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
          title,
          description
        })
      })

      const responseData = await response.json()

      if (!response.ok) {
        throw new Error(responseData.message)
      }
      console.log(responseData);
    } catch (error) {
      console.log(error);
      setError(error.message)
    }
  }

  return (
    <div className="App">
      <div>
        <h1>Create your thoughts</h1>
      </div>
      <form onSubmit={handleSubmit}>
        <div>
          <input type="text" name="title" id="title" placeholder="Enter your thought!" onChange={e => setTitle(e.target.value)}/>
        </div>
        <div>
          <input type="text" name="description" id="description" placeholder="Enter your description!" onChange={e => setDescription(e.target.value)}/>
        </div>
        <div>
          <input type="submit" value="Create" />
        </div>
      </form>
    </div>
  );
}

export default App;
