import './Container.css'
import Card from '../components/Card'
import ErrorMessage from '../components/ErrorMessage'
import { useEffect, useState } from 'react'

const Home = () => {
  const [thoughts, setThoughts] = useState([])
  const [error, setError] = useState('')
  const [searchTerm, setSearchTerm] = useState('')

  useEffect(() => {
    async function getUsers() {
      try {
        const response = await fetch('http://localhost:9000/thoughts/')
        const responseData = await response.json()
        if (!response.ok) {
          throw new Error(responseData.message || 'Not found thoughts, make a register!')
        }
        setThoughts(...[responseData])
      } catch (err) {
        setError(err.message ||"Not found thoughts, make a register!")
      }
    }
    getUsers()
  },[])

  return (
    <div className='container'>
      <h1>Your Thoughts</h1>
      {error && <ErrorMessage>{error}</ErrorMessage>}
      <input type="text" name="searchTerm" id="searchTerm" onChange={e => setSearchTerm(e.target.value)}/>
      {Array.isArray(thoughts.thoughts) && thoughts.thoughts.filter(val => {
        if (searchTerm === "") {
          return val
        } else if (val.title.toLowerCase().includes(searchTerm.toLowerCase())) {
          return val
        }
      }).map((thought, i) => (
        <Card
          key={i}
          title={thought.title}
          description={thought.description}
        />
      ))}
    </div>
  )
}

export default Home
