import Input from "../components/Input"
import Textarea from "../components/Textarea"
import ErrorMessage from "../components/ErrorMessage"
import SuccessMessage from "../components/SuccessMessage"
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"


const Update = () => {
  const [error, setError] = useState('')
  const [success, setSuccess] = useState('')
  const {id} = useParams()
  const [title, setTitle] = useState('')
  const [description, setDescription] = useState('')
  const [thoughts, setThoughts] = useState({title:''})

  useEffect(() => {
    async function edit() {
      const response = await fetch('http://localhost:9000/thoughts/')
      const responseData = await response.json()
      setThoughts(responseData)
    }
    edit()
  }, [id])
  const handleEdit= (e) =>  {
    setThoughts({...thoughts, [e.target.name]:e.target.value})
  }

  console.log(thoughts)
  
  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
        const response = await fetch(`http://localhost:9000/thoughts/edit/${id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
          ...thoughts
        })
      })

      const responseData = await response.json()
    
      if (!response.ok) {
        throw new Error(responseData.message)
      } else {
        setError('')
        setSuccess(responseData.message)
      }
    } catch (err) {
      setSuccess('')
      setError(err.message)
    }
  }
  
  return (
    <div className="container">
      {error && <ErrorMessage>{error}</ErrorMessage>}
      {success && <SuccessMessage>{success}</SuccessMessage>}
      <h1>Edit</h1>
      <form onSubmit={handleSubmit}>
        <Input
          text='Title'
          type='text'
          name='title'
          id='title'
          placeholder='Enter with a title for the thought!'
          onChange={e => handleEdit(e)}
          value={thoughts.title}
        />
        <Textarea
          text='Description'
          type='text'
          name='description'
          id='description'
          placeholder='Enter with a description for the thought!'
          onChange={e => setDescription(e.target.value)}
          value={thoughts.description}
        />

        <input className="btn" type="submit" value="Register" />
      </form>
    </div>
  )
}

export default Update
