import './Container.css'
//import Card from '../components/Card'
import ErrorMessage from '../components/ErrorMessage'
import { useEffect, useState } from 'react'

const Home = () => {
  const [thoughts, setThoughts] = useState([])
  const [error, setError] = useState('')

  useEffect(() => {
    async function getUsers() {
      try {
        const response = await fetch('http://localhost:9000/thoughts/')
        const responseData = await response.json()
        if (!response.ok) {
          throw new Error(responseData.message || 'Not found thoughts, make a register!')
        }
        setThoughts(responseData)
      } catch (err) {
        setError(err.message || "Occured an error, try later!")
      }
    }
    getUsers()
  },[])

  // useEffect(() => {
  //   fetch('http://localhost:9000/thoughts/')
  //   .then(res => res.json())
  //   .then(res => setThoughts(res))
  // },[])

  // useEffect(() => {
    
  //   fetch('http://localhost:9000/thoughts/', )
  //     .then(async (res) => {
  //       const data = await res.json()

  //       if (!res.ok) {
  //         throw new Error(data.message)
  //       } else {
  //         setThoughts(data)
  //       }
  //     }).catch((err) => {setError(err.message)})
      
  // },[setError])

  console.log(thoughts)

  return (
    <div className='container'>
      {error && <ErrorMessage>{error}</ErrorMessage>}
      <h1>Home</h1>
      {Array.isArray(thoughts) && thoughts.map(thought => (
        <div key={thought._id}>
          Title: {thought.title} - Description: {thought.description}
        </div>
      ))}
    </div>
  )
}

export default Home
