import { useRef, useState } from "react"
import ErrorMessage from "../components/ErrorMessage"
import Input from "../components/Input"
import SuccessMessage from "../components/SuccessMessage"
import Textarea from "../components/Textarea"
import styles from './Register.module.css'
import Submit from "../components/Submit"
import { useNavigate } from "react-router-dom"
import InputImage from "../components/InputImage"

const Register = () => {
  const [title, setTitle] = useState('')
  const [description, setDescription] = useState('')
  const [error, setError] = useState('')
  const [success, setSuccess] = useState('')
  const [file, setFile] = useState([])

  const navigate = useNavigate()

  const handleSubmit = async (e) => {
    e.preventDefault()
    
    try {
      const response = await fetch('http://localhost:9000/thoughts/register', {
        method: 'POST',
        headers: {
          'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
          title,
          description,
          
        })
      })

      const responseData = await response.json()

      if (!response.ok) {
        throw new Error(responseData.message)
      } else {
        navigate('/', {
          state: {
            message: responseData.message
          }
        })

        // setError('')
        // setSuccess(responseData.message)
        // setTimeout(() => {
        //   setSuccess('')
        // }, 1000);
        // setTitle('')
        // setDescription('')
      }
    } catch (err) {
      setSuccess('')
      setError(err.message)
    }
  }

  const onInputChange = (e) => {
    setFile(e.target.files[0])
    console.log(file)
  }
  
  const refPick = useRef()

  const pickImage = () => {
    refPick.current.click()
  }
  return (
    <div className={styles.container}>
      <h1>Register</h1>
      <form onSubmit={handleSubmit}>
        <Input
          text='Title'
          type='text'
          name='title'
          id='title'
          placeholder='Enter with a title for the thought!'
          onChange={e => setTitle(e.target.value)}
          value={title}
        />
        <Textarea
          text='Description'
          type='text'
          name='description'
          id='description'
          placeholder='Enter with a description for the thought!'
          onChange={e => setDescription(e.target.value)}
          value={description}
        />
        <InputImage 
          onClick={pickImage}
          onChange={onInputChange} 
          ref={refPick}
          type="file" 
          value="Choose file" 
          name='image'
          accept='.jpg, .png, .jpeg' 
        />
        <Submit type="submit" value="Register" />
      </form>
      {error && <ErrorMessage>{error}</ErrorMessage>}
      {success && <SuccessMessage>{success}</SuccessMessage>}
    </div>
  )
}

export default Register
