import { useState, useEffect } from "react"

const Table = () => {
  const [thoughts, setThoughts] = useState([])

  useEffect(() => {
    async function getThoughts() {
      const response = await fetch('http://localhost:9000/thoughts/')
      const responseData = await response.json()
      setThoughts(responseData)
    }
    getThoughts()
  }, [])

  const value = thoughts.thoughts

  //console.log(Object.keys(value));
  return (
    <div>
      {Array.isArray(value) && value.map((thought) => {
        return (
          <p>{thought._id} - {thought.title} - {thought.description}</p>
        )
      })}
    </div>
  )
}

export default Table
