  const [width, setWidth] = useState('')

  

  console.log(width);

  return (
    <>
    <div 
      className={styles.content}
      contentEditable='true'
      onChange={(e) => setWidth(e.target.value)}
    >{Children}</div>
    <div 
      className={styles.content}
      contentEditable='true'
      //onChange={(e) => setWidth(e.target.value)}
      //value={width}
    >{width}</div>
    <h1>{width}</h1>
    </>
  )
}

export default ContentEditable
